import ReactDOM from "react-dom";

function Clock() {
        return (
            <h1 className="heading">
            <span className="text"> Hello - 
            {new Date().toLocaleTimeString(this.props.locale)}
            </span>

        </h1>
        )
    }


ReactDOM.render(<Clock locale="bn-BD"></Clock>, document.getElementById('root'));

