import React from 'react'; 

class Clock extends React.Component {
    constructor(props){
        super(props);
        this.state = {};
    }
    componentDidMount(){
        setInterval(() => {
            this.tick()
        }, 1000);
    }

    //component unmount 

    tick(){
        this.setState({
            date: new Date(),
        });
    }
    render() {
        return (
            <h1 className="heading">
            <span className="text"> Hello - 
            {new Date().toLocaleTimeString()}
            </span>

        </h1>
        )
    }
}

export default Clock;
